
public class HelloKorea_03 {

	/*
	 * 여러줄짜리 주석
	 * 
	 * 여러줄에 걸쳐 다양한 주석을 작성하고자 할때 
	 * 사용하는 방법
	 * 
	 * 주석을 작성하는 방법
	 * 1. 가급적 문장으로 작성
	 * 2. 나중에 코드를 봤을 때 이해할수 있도록 작성 
	 * 
	 * 
	 * 숫자 30 과 40을 사칙연산을 수행하여
	 * console에 보여주는 코드
	 * 
	 * 	 
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(30+40); //덧셈연산
		System.out.println(30-40); //뺄셈연산
		System.out.println(30*40); //곱셈연산
		System.out.println(40/30); //나눗셈연산

		System.out.println(2100000000+100000000);
	}

}
